from flask_wtf import FlaskForm
from wtforms import SelectField, SubmitField
import os

class choose_form(FlaskForm):
    # https://teamtreehouse.com/community/using-wtforms-for-selectfield-in-flask
    target = [("GDP growth","GDP growth"),("Interest rate","Ineterst rate")]
    target_indicator = SelectField('Please select indicator', choices=target)
    submit = SubmitField('Submit')

